import { Cancel, Room } from "@material-ui/icons";
import axios from "axios";
import { useRef, useState } from "react";
import "./register.css";

export default function Register({ setShowRegister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };
  return (
    <div className="registerContainer">
        <i 
          className="bi bi-globe-europe-africa"
          style={{ fontSize: '24px', marginRight: '10px' }}
        ></i>
        <span style={{ fontWeight: 'bold', fontSize: '20px' }}>World Map Visualizer</span>
      <form onSubmit={handleSubmit}>
        <input autoFocus placeholder="Enter Username" ref={usernameRef} />
        <input type="email" placeholder="Enter Email Adress" ref={emailRef} />
        <input
          type="password"
          min="6"
          placeholder=" Enter Password"
          ref={passwordRef}
        />
        <button className="registerBtn" type="submit">
          Register
        </button>
        {success && (
          <span className="success">All set! The world of maps is yours to pin.</span>
        )}
        {error && <span className="failure">An error occurred. Please log in again.</span>}
      </form>
      <i
      className="bi bi-x loginCancel"
        onClick={() => setShowRegister(false)}
        style={{
          position: 'absolute',
          top: '3px',
          right: '3px',
          cursor: 'pointer',
        }}
      ></i>
    </div>
  );
}
