import axios from "axios";
import { useRef, useState } from "react";


export default function Register({ setShowRegister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };

  const registerContainerStyle = {
    width: '350px',
    height: '350px',
    padding: '20px',
    borderRadius: '5px',
    backgroundColor: 'white',
    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
    margin: 'auto',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'space-between',
  };

  const registerCancelStyle = {
    position: 'absolute',
    top: '3px',
    right: '3px',
    cursor: 'pointer',
  };

  const logoStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#ff4b5c',
    fontWeight: 700,
  };

  const registerBtnStyle = {
    border: 'none',
    padding: '5px',
    backgroundColor: '#ff4b5c',
    color: 'white',
    cursor: 'pointer',
  };

  const successStyle = {
    fontSize: '12px',
    color: 'green',
    textAlign: 'center',
  };

  const failureStyle = {
    fontSize: '12px',
    color: 'red',
    textAlign: 'center',
  };

  return (
    <div style={registerContainerStyle}>
      <div style={logoStyle}>
        <i className="bi bi-globe" style={{ fontSize: '24px', marginRight: '10px' }}></i>
        <span style={{ fontWeight: 'bold', fontSize: '20px' }}>World Map Visualizer</span>
      </div>
      <form onSubmit={handleSubmit}>
        <input autoFocus placeholder="Enter Username" ref={usernameRef} />
        <input type="email" placeholder="Enter Email Address" ref={emailRef} />
        <input
          type="password"
          min="6"
          placeholder="Enter Password"
          ref={passwordRef}
        />
        <button style={registerBtnStyle} type="submit">
          Register
        </button>
        {success && (
          <span style={successStyle}>All set! The world of maps is yours to pin.</span>
        )}
        {error && <span style={failureStyle}>An error occurred. Please log in again.</span>}
      </form>
      <i
        className="bi bi-x loginCancel"
        onClick={() => setShowRegister(false)}
        style={registerCancelStyle}
      ></i>
    </div>
  );
}
