import axios from "axios";
import { useRef, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import '../Register/register.css'; 

export default function Register({ setShowRegister, switchToregister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };
  const toggleForm = () => {  // New toggle function
    setShowRegister(prev => !prev);
}
return (
  <div className="registerContainer d-flex justify-content-center align-items-center">
    <div className="registerWrapper">
      <h2 className="registerTitle" style={{ fontSize: "30px", color: "#4A89F3", fontFamily: "sans-serif" }}>Welcome</h2>
      <h3 className="registerTitle" style={{ fontSize: "28px", color: "#4A89F3", fontFamily: "sans-serif" }}>Register</h3>

      {success ? (
        <>
          <span className="successMessage text-success d-block text-center mb-3" style={{ fontSize: "14px", textAlign: "center", marginBottom: "10px", color: "#34A853" }}>
            All set! The world of maps is yours to pin.
          </span>
          <button onClick={() => setShowRegister(false)} className="btn btn-success btn-block mb-4" style={{
            backgroundColor: "#34A853", 
            border: "none", 
            padding: "10px 15px", 
            borderRadius: "5px", 
            fontSize: "16px", fontWeight: "bold", 
            cursor: "pointer"
          }}>
            Login
          </button>
        </>
      ) : (
        <form onSubmit={handleSubmit}>
          <div className="form-outline mb-4">
            <label className="form-label" style={{ fontSize: "16px", color: "#4A89F3", fontFamily: "sans-serif" }}>Username</label>
            <input autoFocus type="text" id="username" className="form-control" placeholder="Enter Username" ref={usernameRef} />
          </div>
          <div className="form-outline mb-4">
            <label className="form-label" style={{ fontSize: "16px", color: "#4A89F3", fontFamily: "sans-serif" }}>Email</label>
            <input type="email" id="email" className="form-control" placeholder="Enter Email Address" ref={emailRef} />
          </div>
          <div className="form-outline mb-4">
            <label className="form-label" style={{ fontSize: "16px", color: "#4A89F3", fontFamily: "sans-serif" }}>Password</label>
            <input type="password" id="password" className="form-control" placeholder="Enter Password" ref={passwordRef} />
          </div>
          <button type="submit" className="btn btn-primary btn-block mb-4" style={{
            backgroundColor: "#2DA94F", 
            border: "none", 
            padding: "10px 15px", 
            borderRadius: "5px", 
            fontSize: "16px", fontWeight: "bold", 
            cursor: "pointer"
          }}>
            Register
          </button>
          {error && (
            <span className="errorMessage text-danger d-block text-center mb-3" style={{
              color: "red", 
              display: "block", 
              textAlign: "center", 
              marginBottom: "15px", 
              fontFamily: "sans-serif"
            }}>
              An error occurred. Please try again.
            </span>
          )}
        </form>
      )}

      <div className="text-center">
        <p style={{ fontSize: "14px", textAlign: "center", marginBottom: "10px", color: "black" }}>
          Already a member? 
          <a href="#!" onClick={() => setShowRegister(false)} style={{ color: "#34A853", textDecoration: "underline", fontFamily: "sans-serif", marginRight: "5px" }}>Login</a>
        </p>
      </div>

      <i className="bi bi-x registerCancel" onClick={() => setShowRegister(false)} style={{ position: "absolute", top: "10px", right: "10px", cursor: "pointer", fontSize: "1.5rem", color: "#333" }}></i>
    </div>
  </div>
);
}
