import axios from "axios";
import { useRef, useState } from "react";

export default function Login({ setShowLogin, setCurrentUsername, myStorage }) {
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
      username: usernameRef.current.value,
      password: passwordRef.current.value,
    };
    try {
      const res = await axios.post("/users/login", user);
      setCurrentUsername(res.data.username);
      myStorage.setItem('user', res.data.username);
      setShowLogin(false);
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div 
      className="loginContainer"
      style={{
        width: '350px',
        height: '350px',
        padding: '20px',
        borderRadius: '5px',
        backgroundColor: 'white',
        position: 'absolute',
        top: '0',
        bottom: '0',
        left: '0',
        right: '0',
        margin: 'auto',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-between',
      }}
    >
      <div
        className="logo"
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'teal',
          fontWeight: 700,
        }}
      >
        <i 
          className="bi bi-pin-map-fill"
          style={{ fontSize: '24px', marginRight: '10px' }}
        ></i>
        <span style={{ fontWeight: 'bold', fontSize: '20px' }}>World Map Visualizer</span>
      </div>
      <form onSubmit={handleSubmit}>
        <input autoFocus placeholder="Enter your Username" ref={usernameRef} />
        <input
          type="password"
          min="6"
          placeholder="Enter your Password"
          ref={passwordRef}
        />
        <button
          type="submit"
          style={{
            border: 'none',
            padding: '5px',
            width: '100px',
            backgroundColor: '#ff4b5c',
            color: 'white',
            cursor: 'pointer',
            marginTop: '20px',
            justifyContent: 'center',
            marginLeft: 'center',
          }}>
          Login
        </button>

        {error && <span style={{ fontSize: '12px', color: 'red', textAlign: 'center' }}>Something went wrong!</span>}
      </form>
      <i
        className="bi bi-x loginCancel"
        onClick={() => setShowLogin(false)}
        style={{
          position: 'absolute',
          top: '3px',
          right: '3px',
          cursor: 'pointer',
        }}
      ></i>
    </div>
  );
}
