import axios from "axios";
import { useRef, useState } from "react";
import "./login.css";

export default function Login({ setShowLogin, setCurrentUsername, myStorage }) {
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
      username: usernameRef.current.value,
      password: passwordRef.current.value,
    };
    try {
      const res = await axios.post("/users/login", user);
      setCurrentUsername(res.data.username);
      myStorage.setItem('user', res.data.username);
      setShowLogin(false);
    } catch (err) {
      setError(true);
    }
  };

  return (
<div className="loginContainer">
  <div className="logo" style={{ display: 'flex', alignItems: 'center' }}>
    <i className="bi bi-globe-europe-africa" style={{ fontSize: '24px', marginRight: '10px' }}></i>
    <span style={{ fontWeight: 'bold', fontSize: '20px', color:"#ff4b5c" }}>World Map Visualizer</span>
  </div>
      <form onSubmit={handleSubmit}>
        <input autoFocus placeholder="username" ref={usernameRef} />
        <input
          type="password"
          min="6"
          placeholder="password"
          ref={passwordRef}
        />
        <button className="loginBtn" type="submit">
          Login
        </button>
        {error && <span className="failure">Something went wrong!</span>}
      </form>
      <i className="bi bi-x loginCancel" onClick={() => setShowLogin(false)}></i>
    </div>
  );
}
