const API_KEY = "f983725c3821cb6135ab5c138b8597db";
const ICON_URL = "http://openweathermap.org/img/wn/";

export async function fetchWeather(lat, lon) {
  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=${API_KEY}`;

  try {
      const response = await fetch(url);
      if (!response.ok) {
          throw new Error('Network response was not ok');
      }
      const data = await response.json();

      // Extract the icon code from the response data
      const iconCode = data.weather[0].icon;
      const iconUrl = `${ICON_URL}${iconCode}@2x.png`; // URL for the icon image

      // Add the icon URL to the data object that you return
      return { ...data, iconUrl };
  } catch (error) {
      console.error("Error fetching weather data: ", error);
      // Handle the error appropriately in your app
  }
}
