import axios from "axios";
import { useRef, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import '../Register/register.css'; 

export default function Register({ setShowRegister, switchToregister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };
  const toggleForm = () => {  // New toggle function
    setShowRegister(prev => !prev);
}
  return (
    <div className="registerContainer d-flex justify-content-center align-items-center">
        <div className="registerWrapper">
        <h2 className="registerTitle"style={{fontSize: "30px", color: "#4A89F3", fontFamily:"sans-serif"}}>Welcome </h2>
        <h3 className="registerTitle"style={{fontSize: "28px", color: "#4A89F3", fontFamily:"sans-serif"}}>Register</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-outline mb-4">
          <label className="form-label" htmlFor="form2Example1" style={{fontSize: "16px", color: "#4A89F3", fontFamily:"sans-serif"}}>Username</label>
          <input type="text" id="form2Example1" className="form-control" placeholder="Enter your Username" ref={usernameRef} autoFocus />
          </div>
          <div className="form-outline mb-4">
          <label className="form-label" htmlFor="form2Example2"style={{fontSize: "16px", color: "#4A89F3", fontFamily:"sans-serif"}}>Password</label>
          <input type="password" id="form2Example2" className="form-control" placeholder="Enter your Password" ref={passwordRef} />                    
          </div>
          <button type="submit" className="btn btn-primary btn-block mb-4" style={{backgroundColor: "#2DA94F", border: "none", padding: "10px 15px", borderRadius: "5px", fontSize: "16px", fontWeight: "bold", cursor: "pointer"}}>Register</button>

          {success && <span className="text-success d-block text-center mb-3" style={{color:"#2DA94F"}}>All set! The world maps is all yours to pin.</span>}
          {error && <span className="text-danger d-block text-center mb-3" 
          style={{   
            color: "red", 
            display: "block", 
            textAlign: "center", 
            marginBottom: "15px", 
            fontFamily:"sans-serif"}}>An error occurred. Please try again.</span>}

          <div className="text-center">
            <p>Already a member? <a href="#!" onClick={toggleForm}>Login</a></p>
          </div>
        </form>
        <i className="bi bi-x registerCancel" onClick={() => setShowRegister(false)}></i>
      </div>
    </div>
  );
}
