import axios from "axios";
import { useRef, useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import Register from "../Register/Register";
import './login.css';

export default function Login({ setShowLogin, setCurrentUsername, myStorage }) {    
    const [error, setError] = useState(false);
    const [showRegister, setShowRegister] = useState(false);
    const usernameRef = useRef();
    const passwordRef = useRef();
    const [animate, setAnimate] = useState(true);

    useEffect(() => {
      // After 5 seconds, stop the scrolling animation
      const timer = setTimeout(() => {
        setAnimate(false);
      }, 5000);

      return () => clearTimeout(timer);
    }, []);

    const handleSubmitLogin = async (e) => {
        e.preventDefault();
        const user = {
            username: usernameRef.current.value,
            password: passwordRef.current.value,
        };
        try {
            const res = await axios.post("/users/login", user);
            setCurrentUsername(res.data.username);
            myStorage.setItem('user', res.data.username);
            setShowLogin(false);
        } catch (err) {
            setError(true);
        }
    };

    const toggleForm = () => {  
        setShowRegister(prev => !prev);
    }

    return (
      <div className="loginContainer d-flex justify-content-center align-items-center">
          <div className="loginWrapper">
              {showRegister ? (
                  // Render the Register component when showRegister is true
                  <div>
                      <Register />
                  </div>
              ) : (
                  // Render the Login form when showRegister is false
                  <div className="loginTitle">
                  <h2 className={`loginTitle ${animate ? 'scrolling' : ''}`}
                      style={{
                        fontSize: "30px", 
                        color: "#4A89F3", 
                        fontFamily: "sans-serif",
                        overflow: 'hidden'
                      }}>
                    Welcome Back
                  </h2>
                      <h3 className="loginTitle"style={{fontSize: "28px", color: "#4A89F3", fontFamily:"sans-serif"}}>Login</h3>
                      <form onSubmit={handleSubmitLogin}>
                      <div className="form-outline mb-4">
                      <label className="form-label" htmlFor="form2Example1" style={{fontSize: "16px", color: "#4A89F3", fontFamily:"sans-serif"}}>Username</label>
                        <input type="text" id="form2Example1" className="form-control" placeholder="Enter your Username" ref={usernameRef} autoFocus />
                    </div>
                    <div className="form-outline mb-4">
                    <label className="form-label" htmlFor="form2Example2"style={{fontSize: "16px", color: "#4A89F3", fontFamily:"sans-serif"}}>Password</label>
                        <input type="password" id="form2Example2" className="form-control" placeholder="Enter your Password" ref={passwordRef} />                    
                        </div>
                    <button type="submit" className="btn btn-primary btn-block mb-4" 
                    style={{
                        backgroundColor: "#018749", 
                        border: "none", 
                        padding: "10px 15px", 
                        borderRadius: "5px", 
                        fontSize: "16px", fontWeight: "bold", 
                        cursor: "pointer"}}
                        >
                        Sign in
                        </button>
                    {error && <span style={{
                        color: "red", 
                        display: "block", 
                        textAlign: "center", 
                        marginBottom: "15px", 
                        fontFamily:"sans-serif"}}
                        >
                        Something went wrong!
                    </span>}                           
                    <div className="text-center">
                    <p style={{fontSize: "14px", textAlign: "center", marginBottom: "10px", color:"black"}}>
                    Not a member? 
                    <a href="#!" onClick={toggleForm} style={{color: "#018749", textDecoration: "underline", fontFamily:"sans-serif", marginRight:"5px"}}>Register</a>
                    </p>

                        <div>
                        <button type="button" className="btn btn-link btn-floating mx-1" style={{color:"#4A89F3"}}>
                                <i className="fab fa-google"></i>
                            </button>
                            <button type="button" className="btn btn-link btn-floating mx-1" style={{color:"#333333"}}>
                                <i className="fab fa-github"></i>
                            </button>
                            <button type="button" className="btn btn-link btn-floating mx-1">
                                <i class="fa-brands fa-meta"></i>
                            </button>
                        </div>
                    </div>
                </form>
                <i className="bi bi-x loginCancel" onClick={() => setShowLogin(false)}></i>
            </div>
                )}
        </div>
        </div>
    );
}
