import 'bootstrap-icons/font/bootstrap-icons.css';
import axios from "axios";
import { useRef, useState, useContext } from "react";
import { useNavigate } from 'react-router-dom';
import "./register.css";
import { AuthContext } from '../../index';

export default function Register() {
  const navigate = useNavigate();
  const { setIsAuthenticated } = useContext(AuthContext);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
      // If you want to auto-login users after registration
      setIsAuthenticated(true); // Update the authentication state
      navigate("/map"); // Navigate to the map page
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className="registerContainer">
        <div className="logo">
            <i className="bi bi-globe-europe-africa logoIcon"></i>
            <span>World Map Visualizer</span>
        </div>
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="username" /*ref=username between curly braces*/ />
            <input type="email" placeholder="email" /*ref=email between curly braces*/ />
            <input
                type="password"
                min="6"
                placeholder="password"
                ref={passwordRef}
            />
            <button className="registerBtn" type="submit">
                Register
            </button>

            {success && (
                <span className="success">Successful. You can login now!</span>
            )}
            {error && <span className="failure">Something went wrong!</span>}

        </form>
        {/*
        <i className="bi bi-x loginCancel" onClick={() => {
            console.log("Cancel button clicked");
            setShowRegister(false);
        }}></i>
        */}
    </div>
)}
