import axios from "axios";
import { useRef, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import '../Register/register.css'; 

export default function Register({ setShowRegister, switchToregister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };
  const toggleForm = () => {  // New toggle function
    setShowRegister(prev => !prev);
}
  return (
    <div className="registerContainer d-flex justify-content-center align-items-center">
        <div className="registerWrapper">
        <h2 className="registerTitle"style={{fontSize: "30px", color: "#4A89F3", fontFamily:"sans-serif"}}>Welcome </h2>
        <h3 className="registerTitle"style={{fontSize: "28px", color: "#4A89F3", fontFamily:"sans-serif"}}>Register</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-outline mb-4">
            <label className="form-label" style={{fontSize: "16px", color: "#4A89F3", fontFamily:"sans-serif"}}>Username</label>
            <input autoFocus type="text" className="form-control" placeholder="Enter Username" ref={usernameRef} />

          </div>
          <div className="form-outline mb-4">
            <input type="email" className="form-control" placeholder="Enter Email Address" ref={emailRef} />
            <label className="form-label">Email</label>
          </div>
          <div className="form-outline mb-4">
            <input type="password" className="form-control" placeholder="Enter Password" ref={passwordRef} />
            <label className="form-label">Password</label>
          </div>
          <button type="submit" className="btn btn-primary btn-block mb-4">Register</button>

          {success && <span className="text-success d-block text-center mb-3">All set! The world of maps is yours to pin.</span>}
          {error && <span className="text-danger d-block text-center mb-3">An error occurred. Please try again.</span>}

          <div className="text-center">
            <p>Already a member? <a href="#!" onClick={toggleForm}>Login</a></p>
          </div>
        </form>
        <i className="bi bi-x registerCancel" onClick={() => setShowRegister(false)}></i>
      </div>
    </div>
  );
}
