import axios from "axios";
import { useRef, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './login.css';

export default function Login({ setShowLogin, setCurrentUsername, myStorage }) {
    const [error, setError] = useState(false);
    const usernameRef = useRef();
    const passwordRef = useRef();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const user = {
            username: usernameRef.current.value,
            password: passwordRef.current.value,
        };
        try {
            const res = await axios.post("/users/login", user);
            setCurrentUsername(res.data.username);
            myStorage.setItem('user', res.data.username);
            setShowLogin(false);
        } catch (err) {
            setError(true);
        }
    };

    return (
        <div className="loginContainer d-flex justify-content-center align-items-center">
            <div className="loginWrapper">
                <form onSubmit={handleSubmit}>
                <div className="form-outline mb-4">
                    <input type="text" id="form2Example1" className="form-control" placeholder="Enter your Username" ref={usernameRef} autoFocus />
                    <label className="form-label" htmlFor="form2Example1">Username</label>
                </div>
                <div className="form-outline mb-4">
                    <input type="password" id="form2Example2" className="form-control" placeholder="Enter your Password" ref={passwordRef} />
                    <label className="form-label" htmlFor="form2Example2">Password</label>
                </div>
                <div className="row mb-4">


                </div>
                <button type="submit" className="btn btn-primary btn-block mb-4">Sign in</button>
                {error && <span className="text-danger d-block text-center mb-3">Something went wrong!</span>}
                <div className="text-center">
                    <p>Not a member? <a href="#!">Register</a></p>
                    <p>or sign up with:</p>
                    <div>
                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-facebook-f"></i>
                        </button>
                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-google"></i>
                        </button>
                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-twitter"></i>
                        </button>
                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-github"></i>
                        </button>
                    </div>
                </div>
            </form>
            <i className="bi bi-x loginCancel" onClick={() => setShowLogin(false)}></i>
        </div>
        </div>
    );
}
