import axios from "axios";
import { useRef, useState } from "react";

export default function Register({ setShowRegister }) {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      password: passwordRef.current.value,
    };

    try {
      await axios.post("/users/register", newUser);
      setError(false);
      setSuccess(true);
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div style={{
      width: '350px',
      height: '350px',
      padding: '20px',
      borderRadius: '5px',
      backgroundColor: 'white',
      position: 'absolute',
      top: 0,
      bottom: 0,
      left: 0,
      right: 0,
      margin: 'auto',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'space-between',
    }}>
      <form onSubmit={handleSubmit} style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100%',
      }}>
        <input autoFocus placeholder="Enter Username" ref={usernameRef} />
        <input type="email" placeholder="Enter Email Address" ref={emailRef} />
        <input type="password" min="6" placeholder="Enter Password" ref={passwordRef} />

        <button type="submit" style={{
          border: 'none',
          padding: '10px 20px',
          backgroundColor: '#ff4b5c',
          color: 'white',
          cursor: 'pointer',
          marginTop: '20px',
          width: '100px'
        }}>
          Register
        </button>

        {success && (
          <span style={{
            fontSize: '12px',
            color: 'green',
            textAlign: 'center',
          }}>All set! The world of maps is yours to pin.</span>
        )}
        {error && (
          <span style={{
            fontSize: '12px',
            color: 'red',
            textAlign: 'center',
          }}>An error occurred. Please try again.</span>
        )}
      </form>
      <i
        className="bi bi-x loginCancel"
        onClick={() => setShowRegister(false)}
        style={{
          position: 'absolute',
          top: '3px',
          right: '3px',
          cursor: 'pointer',
        }}
      ></i>
    </div>
  );
}
