import axios from "axios";
import 'bootstrap-icons/font/bootstrap-icons.css';
import { useRef, useState, useContext } from "react";
import { useNavigate } from 'react-router-dom';
import "./login.css";
import { AuthContext } from '../../index'

export default function Login({ setCurrentUsername, myStorage }) {
  const navigate = useNavigate();
  const { setIsAuthenticated } = useContext(AuthContext);
  const [error, setError] = useState(false);
  const usernameRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = {
      username: usernameRef.current.value,
      password: passwordRef.current.value,
    };
    try {
      const res = await axios.post("/users/login", user);
      setCurrentUsername(res.data.username);
      myStorage.setItem('user', res.data.username);
      setIsAuthenticated(true); // Update the authentication state
      navigate("/map"); // Navigate to the map page
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className="loginContainer">
      <div className="logo">
      <i className="bi bi-globe-europe-africa logoIcon"></i>
        <span>World Map Visualizer</span>
      </div>
      <form onSubmit={handleSubmit}>
        <input autoFocus placeholder="username" ref={usernameRef} />
        <input
          type="password"
          min="6"
          placeholder="password"
          ref={passwordRef}
        />
        <button className="loginBtn" type="submit">
          Login
        </button>
        {error && <span className="failure">Something went wrong!</span>}
      </form>
      {/* <i className="bi bi-x loginCancel" onClick={() => {
        console.log("Cancel button clicked");
        setShowLogin(false);
     }}></i> */}

    </div>
  );
}
